import React from 'react';
import { Toilet } from '../types';

interface LegendProps {
  userLocationExists: boolean;
}

const Legend: React.FC<LegendProps> = React.memo(({ userLocationExists }) => {
  return (
    <div className="p-3 bg-white border-bottom">
      <h6 className="mb-2">Легенда:</h6>
      <div className="d-flex gap-4 flex-wrap">
        <div className="d-flex align-items-center gap-2">
          <div style={{
            width: '16px', 
            height: '16px', 
            borderRadius: '50%', 
            backgroundColor: '#28a745', 
            border: '2px solid white', 
            boxShadow: '0 1px 2px rgba(0,0,0,0.3)'
          }}></div>
          <span className="small">Бесплатный</span>
        </div>
        <div className="d-flex align-items-center gap-2">
          <div style={{
            width: '16px', 
            height: '16px', 
            borderRadius: '50%', 
            backgroundColor: '#dc3545', 
            border: '2px solid white', 
            boxShadow: '0 1px 2px rgba(0,0,0,0.3)'
          }}></div>
          <span className="small">Платный</span>
        </div>
        <div className="d-flex align-items-center gap-2">
          <div style={{
            width: '16px', 
            height: '16px', 
            borderRadius: '50%', 
            backgroundColor: '#ffc107', 
            border: '2px solid white', 
            boxShadow: '0 1px 2px rgba(0,0,0,0.3)'
          }}></div>
          <span className="small">Только за покупку</span>
        </div>
        {userLocationExists && (
          <div className="d-flex align-items-center gap-2">
            <div style={{
              width: '16px', 
              height: '16px', 
              borderRadius: '50%', 
              backgroundColor: '#007bff', 
              border: '2px solid white', 
              boxShadow: '0 1px 2px rgba(0,0,0,0.3)'
            }}></div>
            <span className="small">Ваше местоположение</span>
          </div>
        )}
      </div>
    </div>
  );
});

Legend.displayName = 'Legend';

export default Legend;
