import React, { useState, useCallback } from 'react';
import { Modal, Button, Form, Alert, Spinner } from 'react-bootstrap';
import { ToiletFormData, Toilet } from '../types';
import { validateAddress, geocodeAddress, formatAddress } from '../services/geocoding';

interface AddToiletModalProps {
  show: boolean;
  onHide: () => void;
  onSubmit: (data: ToiletFormData) => Promise<void>;
  loading: boolean;
}

const AddToiletModal: React.FC<AddToiletModalProps> = React.memo(({
  show,
  onHide,
  onSubmit,
  loading
}) => {
  const [newToilet, setNewToilet] = useState<ToiletFormData>({
    name: '',
    address: '',
    latitude: 0,
    longitude: 0,
    type: 'free',
    price: '',
    description: ''
  });
  
  const [geocodingLoading, setGeocodingLoading] = useState(false);
  const [addressError, setAddressError] = useState('');

  const handleAddressChange = useCallback(async (address: string) => {
    setNewToilet(prev => ({ ...prev, address }));
    
    // –í–∞–ª–∏–¥–∞—Ü–∏—è –∞–¥—Ä–µ—Å–∞
    if (address && !validateAddress(address)) {
      setAddressError('–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –∞–¥—Ä–µ—Å (—É–ª–∏—Ü–∞ –∏ –Ω–æ–º–µ—Ä –¥–æ–º–∞)');
      return;
    }
    
    setAddressError('');
    
    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç –ø—Ä–∏ –≤–≤–æ–¥–µ –∞–¥—Ä–µ—Å–∞
    if (address && validateAddress(address)) {
      setGeocodingLoading(true);
      try {
        const result = await geocodeAddress(address);
        if (result && result.lat) {
          const lat = parseFloat(result.lat);
          const lng = parseFloat(result.lon);
          
          // –ü—Ä–æ–≤–µ—Ä–∫–∞, —á—Ç–æ –∫–æ–æ—Ä–∞—Ç—ã –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö –ö–∞–ª–∏–Ω–∏–Ω–≥—Ä–∞–¥–∞
          if (lat >= 54.6 && lat <= 54.8 && lng >= 20.3 && lng <= 20.6) {
            setNewToilet(prev => ({
              ...prev,
              latitude: lat,
              longitude: lng,
              address: formatAddress(address)
            }));
            setAddressError('');
          } else {
            setAddressError('–ê–¥—Ä–µ—Å –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –∑–∞ –ø—Ä–µ–¥–µ–ª–∞–º–∏ –ö–∞–ª–∏–Ω–∏–Ω–≥—Ä–∞–¥–∞');
          }
        } else {
          setAddressError('–ê–¥—Ä–µ—Å –Ω–µ –Ω–∞–π–¥–µ–Ω. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø—Ä–∞–≤–∏–ª—å–Ω–æ—Å—Ç—å –≤–≤–µ–¥–µ–Ω–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö');
        }
      } catch (error) {
        setAddressError('–û—à–∏–±–∫–∞ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑');
      } finally {
        setGeocodingLoading(false);
      }
    }
  }, []);

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    
    // –§–∏–Ω–∞–ª—å–Ω–∞—è –≤–∞–ª–∏–¥–∞—Ü–∏—è
    if (!newToilet.name || !newToilet.address) {
      return;
    }

    if (!validateAddress(newToilet.address)) {
      setAddressError('–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –∞–¥—Ä–µ—Å (—É–ª–∏—Ü–∞ –∏ –Ω–æ–º–µ—Ä –¥–æ–º–∞)');
      return;
    }

    if (!newToilet.latitude || !newToilet.longitude) {
      setAddressError('–ù–µ —É–¥–∞–ª–æ—Å—å –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –∞–¥—Ä–µ—Å');
      return;
    }

    await onSubmit(newToilet);
    resetForm();
  }, [newToilet, onSubmit]);

  const resetForm = useCallback(() => {
    setNewToilet({
      name: '',
      address: '',
      latitude: 0,
