import React, { useState, useEffect } from 'react';
import { Tab, Tabs, Table, Button, Modal, Form, Badge, Alert, Card, Row, Col } from 'react-bootstrap';
import { Toilet, ToiletFormData, Admin } from '../types';
import { adminAPI, authAPI } from '../services/api';

const AdminPanel: React.FC = () => {
  const [admin, setAdmin] = useState<Admin | null>(null);
  const [toilets, setToilets] = useState<Toilet[]>([]);
  const [pendingToilets, setPendingToilets] = useState<Toilet[]>([]);
  const [approvedToilets, setApprovedToilets] = useState<Toilet[]>([]);
  const [loading, setLoading] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingToilet, setEditingToilet] = useState<Toilet | null>(null);
  const [showRegisterModal, setShowRegisterModal] = useState(false);
  const [registerData, setRegisterData] = useState({ username: '', password: '' });
  const [alert, setAlert] = useState<{ show: boolean; message: string; variant: string }>({
    show: false,
    message: '',
    variant: 'success'
  });

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    const token = localStorage.getItem('adminToken');
    if (!token) {
      setShowRegisterModal(true);
      return;
    }

    try {
      const profileData = await authAPI.getProfile();
      setAdmin(profileData.user);
      loadData();
    } catch (error) {
      localStorage.removeItem('adminToken');
      setShowRegisterModal(true);
    }
  };

  const loadData = async () => {
    setLoading(true);
    try {
      const [allData, pendingData, approvedData] = await Promise.all([
        adminAPI.getAllToilets(),
        adminAPI.getAllToilets('pending'),
        adminAPI.getAllToilets('approved')
      ]);

      setToilets(allData);
      setPendingToilets(pendingData);
      setApprovedToilets(approvedData);
    } catch (error) {
      console.error('Error loading data:', error);
      setAlert({
        show: true,
        message: 'Ошибка загрузки данных',
        variant: 'danger'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const data = await authAPI.register(registerData.username, registerData.password);
      localStorage.setItem('adminToken', data.token);
      setAdmin(data.admin);
      setShowRegisterModal(false);
      setAlert({
        show: true,
        message: 'Администратор успешно зарегистрирован',
        variant: 'success'
      });
      loadData();
    } catch (error: any) {
      setAlert({
        show: true,
        message: error.response?.data?.error || 'Ошибка регистрации',
        variant: 'danger'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const data = await authAPI.login(registerData.username, registerData.password);
      localStorage.setItem('adminToken', data.token);
      setAdmin(data.admin);
      setShowRegisterModal(false);
      setAlert({
        show: true,
        message: 'Вход выполнен успешно',
        variant: 'success'
      });
      loadData();
    } catch (error: any) {
      setAlert({
        show: true,
        message: error.response?.data?.error || 'Ошибка входа',
        variant: 'danger'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (id: number) => {
    try {
      await adminAPI.approveToilet(id);
      setAlert({
        show: true,
        message: 'Туалет одобрен',
        variant: 'success'
      });
      loadData();
    } catch (error: any) {
      setAlert({
        show: true,
        message: error.response?.data?.error || 'Ошибка одобрения',
        variant: 'danger'
      });
    }
  };

  const handleDelete = async (id: number) => {
    if (!window.confirm('Вы уверены, что хотите удалить этот туалет?')) {
      return;
    }

    try {
      await adminAPI.deleteToilet(id);
      setAlert({
        show: true,
        message: 'Туалет удален',
        variant: 'success'
      });
      loadData();
    } catch (error: any) {
      setAlert({
        show: true,
        message: error.response?.data?.error || 'Ошибка удаления',
        variant: 'danger'
      });
    }
  };

  const handleEdit = (toilet: Toilet) => {
    setEditingToilet(toilet);
    setShowEditModal(true);
  };

  const handleUpdate = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingToilet) return;

    setLoading(true);
    try {
      await adminAPI.updateToilet(editingToilet.id!, editingToilet as ToiletFormData);
      setAlert({
        show: true,
        message: 'Туалет обновлен',
        variant: 'success'
      });
      setShowEditModal(false);
      setEditingToilet(null);
      loadData();
    } catch (error: any) {
      setAlert({
        show: true,
        message: error.response?.data?.error || 'Ошибка обновления',
        variant: 'danger'
      });
    } finally {
      setLoading(false);
    }
  };

  const getTypeLabel = (type: Toilet['type']) => {
    const labels = {
      free: 'Бесплатный',
      paid: 'Платный',
      purchase_required: 'Только за покупку'
    };
    return labels[type];
  };

  const getTypeBadgeVariant = (type: Toilet['type']) => {
    const variants = {
      free: 'success',
      paid: 'warning',
      purchase_required: 'danger'
    };
    return variants[type];
  };

  const ToiletTable: React.FC<{ toilets: Toilet[]; showActions?: boolean }> = ({ toilets, showActions = true }) => (
    <Table striped bordered hover responsive>
      <thead>
        <tr>
          <th>ID</th>
          <th>Название</th>
          <th>Адрес</th>
          <th>Тип</th>
          <th>Стоимость</th>
          <th>Координаты</th>
          <th>Дата добавления</th>
          {showActions && <th>Действия</th>}
        </tr>
      </thead>
      <tbody>
        {toilets.map((toilet) => (
          <tr key={toilet.id}>
            <td>{toilet.id}</td>
            <td>{toilet.name}</td>
            <td>{toilet.address}</td>
            <td>
              <Badge bg={getTypeBadgeVariant(toilet.type)}>
                {getTypeLabel(toilet.type)}
              </Badge>
            </td>
            <td>{toilet.price || '-'}</td>
            <td>
              <small>
                {toilet.latitude.toFixed(6)}, {toilet.longitude.toFixed(6)}
              </small>
            </td>
            <td>
              <small>{new Date(toilet.created_at!).toLocaleDateString()}</small>
            </td>
            {showActions && (
              <td>
                <div className="btn-group" role="group">
                  {!toilet.approved && (
                    <Button
                      variant="success"
                      size="sm"
                      onClick={() => handleApprove(toilet.id!)}
                    >
                      ✓
                    </Button>
                  )}
                  <Button
                    variant="primary"
                    size="sm"
                    onClick={() => handleEdit(toilet)}
                  >
                    ✏️
                  </Button>
                  <Button
                    variant="danger"
                    size="sm"
                    onClick={() => handleDelete(toilet.id!)}
                  >
                    🗑️
                  </Button>
                </div>
              </td>
            )}
          </tr>
        ))}
      </tbody>
    </Table>
  );

  if (!admin) {
    return (
      <Modal show={showRegisterModal} backdrop="static" keyboard={false}>
        <Modal.Header>
          <Modal.Title>Авторизация администратора</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleLogin}>
          <Modal.Body>
            {alert.show && (
              <Alert variant={alert.variant} dismissible onClose={() => setAlert(prev => ({ ...prev, show: false }))}>
                {alert.message}
              </Alert>
            )}

            <Form.Group className="mb-3">
              <Form.Label>Имя пользователя</Form.Label>
              <Form.Control
                type="text"
                required
                value={registerData.username}
                onChange={(e) => setRegisterData(prev => ({ ...prev, username: e.target.value }))}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Пароль</Form.Label>
              <Form.Control
                type="password"
                required
                value={registerData.password}
                onChange={(e) => setRegisterData(prev => ({ ...prev, password: e.target.value }))}
              />
            </Form.Group>

            <Alert variant="info">
              Если это первый запуск, зарегистрируйте администратора. В противном случае войдите с существующими данными.
            </Alert>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleRegister} disabled={loading}>
              {loading ? 'Регистрация...' : 'Зарегистрировать'}
            </Button>
            <Button variant="primary" type="submit" disabled={loading}>
              {loading ? 'Вход...' : 'Войти'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    );
  }

  return (
    <div className="container-fluid p-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Админ панель</h1>
        <div>
          <span className="me-3">Привет, {admin.username}!</span>
          <Button 
            variant="outline-secondary" 
            onClick={() => {
              localStorage.removeItem('adminToken');
              window.location.reload();
            }}
          >
            Выйти
          </Button>
        </div>
      </div>

      {alert.show && (
        <Alert 
          variant={alert.variant} 
          dismissible 
          onClose={() => setAlert(prev => ({ ...prev, show: false }))}
        >
          {alert.message}
        </Alert>
      )}

      <Row className="mb-4">
        <Col md={4}>
          <Card bg="primary" text="white">
            <Card.Body>
              <Card.Title>Всего туалетов</Card.Title>
              <h2>{toilets.length}</h2>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card bg="success" text="white">
            <Card.Body>
              <Card.Title>Одобрено</Card.Title>
              <h2>{approvedToilets.length}</h2>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card bg="warning" text="white">
            <Card.Body>
              <Card.Title>На модерации</Card.Title>
              <h2>{pendingToilets.length}</h2>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Tabs defaultActiveKey="pending" className="mb-3">
        <Tab eventKey="pending" title={`На модерации (${pendingToilets.length})`}>
          {pendingToilets.length === 0 ? (
            <Alert variant="info">Нет туалетов на модерации</Alert>
          ) : (
            <ToiletTable toilets={pendingToilets} />
          )}
        </Tab>
        <Tab eventKey="approved" title={`Одобренные (${approvedToilets.length})`}>
          {approvedToilets.length === 0 ? (
            <Alert variant="info">Нет одобренных туалетов</Alert>
          ) : (
            <ToiletTable toilets={approvedToilets} />
          )}
        </Tab>
        <Tab eventKey="all" title={`Все (${toilets.length})`}>
          <ToiletTable toilets={toilets} />
        </Tab>
      </Tabs>

      <Modal show={showEditModal} onHide={() => setShowEditModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Редактировать туалет</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleUpdate}>
          <Modal.Body>
            {editingToilet && (
              <>
                <Form.Group className="mb-3">
                  <Form.Label>Название</Form.Label>
                  <Form.Control
                    type="text"
                    required
                    value={editingToilet.name}
                    onChange={(e) => setEditingToilet(prev => prev ? { ...prev, name: e.target.value } : null)}
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Адрес</Form.Label>
                  <Form.Control
                    type="text"
                    required
                    value={editingToilet.address}
                    onChange={(e) => setEditingToilet(prev => prev ? { ...prev, address: e.target.value } : null)}
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Тип</Form.Label>
                  <Form.Select
                    required
                    value={editingToilet.type}
                    onChange={(e) => setEditingToilet(prev => prev ? { 
                      ...prev, 
                      type: e.target.value as Toilet['type'] 
                    } : null)}
                  >
                    <option value="free">🆓 Бесплатный</option>
                    <option value="paid">💰 Платный</option>
                    <option value="purchase_required">🛒 Только за покупку</option>
                  </Form.Select>
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Стоимость / Условия</Form.Label>
                  <Form.Control
                    type="text"
                    value={editingToilet.price || ''}
                    onChange={(e) => setEditingToilet(prev => prev ? { ...prev, price: e.target.value } : null)}
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Описание</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={editingToilet.description || ''}
                    onChange={(e) => setEditingToilet(prev => prev ? { ...prev, description: e.target.value } : null)}
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Координаты</Form.Label>
                  <Row>
                    <Col>
                      <Form.Control
                        type="number"
                        step="any"
                        required
                        value={editingToilet.latitude}
                        onChange={(e) => setEditingToilet(prev => prev ? { ...prev, latitude: parseFloat(e.target.value) } : null)}
                        placeholder="Широта"
                      />
                    </Col>
                    <Col>
                      <Form.Control
                        type="number"
                        step="any"
                        required
                        value={editingToilet.longitude}
                        onChange={(e) => setEditingToilet(prev => prev ? { ...prev, longitude: parseFloat(e.target.value) } : null)}
                        placeholder="Долгота"
                      />
                    </Col>
                  </Row>
                </Form.Group>
              </>
            )}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowEditModal(false)}>
              Отмена
            </Button>
            <Button variant="primary" type="submit" disabled={loading}>
              {loading ? 'Сохранение...' : 'Сохранить'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </div>
  );
};

export default AdminPanel;
