import React, { useState, useCallback } from 'react';
import { Modal, Button, Form, Alert, Spinner } from 'react-bootstrap';
import { ToiletFormData, Toilet } from '../types';
import { validateAddress, geocodeAddress, formatAddress } from '../services/geocoding';

interface AddToiletModalProps {
  show: boolean;
  onHide: () => void;
  onSubmit: (data: ToiletFormData) => Promise<void>;
  loading: boolean;
}

const AddToiletModal: React.FC<AddToiletModalProps> = React.memo(({
  show,
  onHide,
  onSubmit,
  loading
}) => {
  const [newToilet, setNewToilet] = useState<ToiletFormData>({
    name: '',
    address: '',
    latitude: 0,
    longitude: 0,
    type: 'free',
    price: '',
    description: ''
  });
  
  const [geocodingLoading, setGeocodingLoading] = useState(false);
  const [addressError, setAddressError] = useState('');

  const handleAddressChange = useCallback(async (address: string) => {
    setNewToilet(prev => ({ ...prev, address }));
    
    // –í–∞–ª–∏–¥–∞—Ü–∏—è –∞–¥—Ä–µ—Å–∞
    if (address && !validateAddress(address)) {
      setAddressError('–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –∞–¥—Ä–µ—Å (—É–ª–∏—Ü–∞ –∏ –Ω–æ–º–µ—Ä –¥–æ–º–∞)');
      return;
    }
    
    setAddressError('');
    
    // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç –ø—Ä–∏ –≤–≤–æ–¥–µ –∞–¥—Ä–µ—Å–∞
    if (address && validateAddress(address)) {
      setGeocodingLoading(true);
      try {
        const result = await geocodeAddress(address);
        if (result && result.lat) {
          const lat = parseFloat(result.lat);
          const lng = parseFloat(result.lon);
          
          // –ü—Ä–æ–≤–µ—Ä–∫–∞, —á—Ç–æ –∫–æ–æ—Ä–∞—Ç—ã –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö –ö–∞–ª–∏–Ω–∏–Ω–≥—Ä–∞–¥–∞
          if (lat >= 54.6 && lat <= 54.8 && lng >= 20.3 && lng <= 20.6) {
            setNewToilet(prev => ({
              ...prev,
              latitude: lat,
              longitude: lng,
              address: formatAddress(address)
            }));
            setAddressError('');
          } else {
            setAddressError('–ê–¥—Ä–µ—Å –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –∑–∞ –ø—Ä–µ–¥–µ–ª–∞–º–∏ –ö–∞–ª–∏–Ω–∏–Ω–≥—Ä–∞–¥–∞');
          }
        } else {
          setAddressError('–ê–¥—Ä–µ—Å –Ω–µ –Ω–∞–π–¥–µ–Ω. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø—Ä–∞–≤–∏–ª—å–Ω–æ—Å—Ç—å –≤–≤–µ–¥–µ–Ω–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö');
        }
      } catch (error) {
        setAddressError('–û—à–∏–±–∫–∞ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑');
      } finally {
        setGeocodingLoading(false);
      }
    }
  }, []);

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    
    // –§–∏–Ω–∞–ª—å–Ω–∞—è –≤–∞–ª–∏–¥–∞—Ü–∏—è
    if (!newToilet.name || !newToilet.address) {
      return;
    }

    if (!validateAddress(newToilet.address)) {
      setAddressError('–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –∞–¥—Ä–µ—Å (—É–ª–∏—Ü–∞ –∏ –Ω–æ–º–µ—Ä –¥–æ–º–∞)');
      return;
    }

    if (!newToilet.latitude || !newToilet.longitude) {
      setAddressError('–ù–µ —É–¥–∞–ª–æ—Å—å –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –∞–¥—Ä–µ—Å');
      return;
    }

    await onSubmit(newToilet);
    resetForm();
  }, [newToilet, onSubmit]);

  const resetForm = useCallback(() => {
    setNewToilet({
      name: '',
      address: '',
      latitude: 0,
      longitude: 0,
      type: 'free',
      price: '',
      description: ''
    });
    setAddressError('');
  }, []);

  const handleHide = useCallback(() => {
    resetForm();
    onHide();
  }, [resetForm, onHide]);

  return (
    <Modal show={show} onHide={handleHide} size="lg">
      <Modal.Header closeButton>
        <Modal.Title>–î–æ–±–∞–≤–∏—Ç—å –Ω–æ–≤—É—é —Ç–æ—á–∫—É</Modal.Title>
      </Modal.Header>
      <Form onSubmit={handleSubmit}>
        <Modal.Body>
          <Alert variant="info">
            <strong>–ò–Ω—Å—Ç—Ä—É–∫—Ü–∏—è:</strong> –í–≤–µ–¥–∏—Ç–µ –∞–¥—Ä–µ—Å –≤ —Ñ–æ—Ä–º–∞—Ç–µ "—É–ª. –õ–µ–Ω–∏–Ω—Å–∫–∏–π –ø—Ä–æ—Å–ø–µ–∫—Ç 30" –∏–ª–∏ "–ö–∏—Ä–æ–≤–∞ 15". 
            –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –æ–ø—Ä–µ–¥–µ–ª—è—Ç—Å—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏.
          </Alert>
          
          <Form.Group className="mb-3">
            <Form.Label>–ù–∞–∑–≤–∞–Ω–∏–µ *</Form.Label>
            <Form.Control
              type="text"
              required
              value={newToilet.name}
              onChange={(e) => setNewToilet(prev => ({ ...prev, name: e.target.value }))}
              placeholder="–ù–∞–ø—Ä–∏–º–µ—Ä: –¢—É–∞–ª–µ—Ç –≤ –¢–¶"
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>–ê–¥—Ä–µ—Å *</Form.Label>
            <div className="position-relative">
              <Form.Control
                type="text"
                required
                value={newToilet.address}
                onChange={(e) => handleAddressChange(e.target.value)}
                placeholder="–ù–∞–ø—Ä–∏–º–µ—Ä: —É–ª. –õ–µ–Ω–∏–Ω—Å–∫–∏–π –ø—Ä–æ—Å–ø–µ–∫—Ç 30"
                isInvalid={!!addressError}
                disabled={geocodingLoading}
              />
              {geocodingLoading && (
                <div className="position-absolute" style={{ right: '10px', top: '8px' }}>
                  <Spinner animation="border" size="sm" />
                </div>
              )}
              <Form.Control.Feedback type="invalid">
                {addressError}
              </Form.Control.Feedback>
              <Form.Text className="text-muted">
                –£–∫–∞–∂–∏—Ç–µ —É–ª–∏—Ü—É –∏ –Ω–æ–º–µ—Ä –¥–æ–º–∞. –ù–∞–ø—Ä–∏–º–µ—Ä: "–ö–∏—Ä–æ–≤–∞ 15" –∏–ª–∏ "—É–ª. –õ–µ–Ω–∏–Ω–∞ 30"
              </Form.Text>
            </div>
          </Form.Group>

          {newToilet.latitude && newToilet.longitude && (
            <Alert variant="success" className="small">
              ‚úÖ –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –æ–ø—Ä–µ–¥–µ–ª–µ–Ω—ã: {newToilet.latitude.toFixed(6)}, {newToilet.longitude.toFixed(6)}
            </Alert>
          )}

          <Form.Group className="mb-3">
            <Form.Label>–¢–∏–ø *</Form.Label>
            <Form.Select
              required
              value={newToilet.type}
              onChange={(e) => setNewToilet(prev => ({ 
                ...prev, 
                type: e.target.value as Toilet['type'] 
              }))}
            >
              <option value="free">frei –ë–µ—Å–ø–ª–∞—Ç–Ω—ã–π</option>
              <option value="paid">üí∞ –ü–ª–∞—Ç–Ω—ã–π</option>
              <option value="purchase_required">üõí –¢–æ–ª—å–∫–æ –∑–∞ –ø–æ–∫—É–ø–∫—É</option>
            </Form.Select>
          </Form.Group>

          {(newToilet.type === 'paid' || newToilet.type === 'purchase_required') && (
            <Form.Group className="mb-3">
              <Form.Label>–°—Ç–æ–∏–º–æ—Å—Ç—å / –£—Å–ª–æ–≤–∏—è</Form.Label>
              <Form.Control
                type="text"
                value={newToilet.price}
                onChange={(e) => setNewToilet(prev => ({ ...prev, price: e.target.value }))}
                placeholder="–ù–∞–ø—Ä–∏–º–µ—Ä: 50 —Ä—É–±–ª–µ–π / –ü–æ–∫—É–ø–∫–∞ –æ—Ç 100 —Ä—É–±–ª–µ–π"
              />
            </Form.Group>
          )}

          <Form.Group className="mb-3">
            <Form.Label>–û–ø–∏—Å–∞–Ω–∏–µ</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={newToilet.description}
              onChange={(e) => setNewToilet(prev => ({ ...prev, description: e.target.value }))}
              placeholder="–î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è (—á–∞—Å—ã —Ä–∞–±–æ—Ç—ã, –¥–æ—Å—Ç—É–ø–Ω–æ—Å—Ç—å –∏ —Ç.–¥.)"
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleHide}>
            –û—Ç–º–µ–Ω–∞
          </Button>
          <Button 
            variant="primary" 
            type="submit" 
            disabled={loading || geocodingLoading || !newToilet.latitude || !!addressError}
          >
            {loading ? '–û—Ç–ø—Ä–∞–≤–∫–∞...' : '–û—Ç–ø—Ä–∞–≤–∏—Ç—å –Ω–∞ –º–æ–¥–µ—Ä–∞—Ü–∏—é'}
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
});

AddToiletModal.displayName = 'AddToiletModal';

export default AddToiletModal;
